$("#programs").click(function(){document.getElementById("api").classList.toggle("show")});$("#contact").click(function(){document.getElementById("email").classList.toggle("show")});window.onclick=function(b){if(!b.target.matches(".dropbtn")){var f=document.getElementsByClassName("dropdown-content");for(var c=0;c<f.length;c++){var a=f[c];if(a.classList.contains("show")){a.classList.remove("show")}}}};